sections.code:
* {
  margin: 1px;
  padding: 1px;
}
@media screen and (min-width: 800px) {
  body {
    max-width: 70%;
  }
}
body {

  margin: auto;
}
main {
  border-radius: 20px;
  padding-top: 1rem;
}

textarea.form-input {
  padding: 0.5rem 0.5rem;
  border-radius: 20px;
}

.topic-voting-label {
  border-radius: 0px;
}

.link-group {
  margin: 2px;
  background-color: #44475a;
  border-radius: 15px;
  padding: 0.2rem 0.5rem;
}

.topic-metadata {
  overflow: visible;
}
.nav .nav-group-list li {
  margin-bottom: 0.3rem;
}

.nav li {
  font-size: 0.8rem;
  text-decoration: none;
}

.btn {
  border-radius: 20px;
}
.btn-link-minimal {
  padding: 0.15rem 0.4rem;
}
.heading-notification > * {
  line-height: 1.2rem;
}
.heading-notification .link-group {
  font-size: 0.7rem;
  white-space: nowrap;
  text-decoration: none;
  padding: 0.07rem 0.3rem;
}

.nav-item {
  padding: 0.2rem 0rem;
}
.tab {
  border-width: 0px;
  background-color: var(--background-mixed-color);
  border-radius: 20px;
}

.tab-item {
  border-radius: 20px;
  padding-left: 0.5rem;
  padding-right: 0.5rem;
}
.tab-item a {
  text-align: center;
  width: 100%;
  border-radius: 20px;
}

.tab-item:hover {
  background-color: var(--button-transparent-color);
  color: var(--button-color);
}

.active {

  border-radius: 20px;
  text-decoration: none;
  background-color: var(--button-transparent-color);
  color: var(--button-color);
}

.tab-listing-order {
  overflow: scroll;
}

.tab .tab-item a {
  border-width: 0px;
}

.topic-listing > li {
  color: var(--foreground-mixed-color);
  background-color: var(--background-mixed-color);
  margin-bottom: 10px;
  border-radius: 20px;
}

.label {
  border-radius: 10px;
  padding: 2px 5px;
}

.comment[data-comment-depth="0"] {
  border-bottom: 2px solid var(--border-color2);
}


.comment {
  border-left: 2px solid var(--button-color);
  border-radius: 20px;
  overflow: hidden;
  padding: 0px;
  margin-left: 0;
  margin-top: 0.5rem;
}
.comment-header {
  overflow: hidden;
  margin: 0px;
  border-radius: 20px;
}

.btn-comment-collapse {
  padding: 0.3rem 0.6rem;
  margin: 0 0.1rem;
}
.btn-comment-collapse-label {
  font-size: 1rem
}

.btn-used {
  color: var(--button-color);
}
#sidebar {
  padding-top: 1rem;
  border-radius: 20px;
  /*display: flex;
  flex-direction: column;*/
}

.btn.btn-link {

  align-self: center;
  border: 1px solid var(--button-color);
  font-size: 0.3rem;
  height: 1.5rem;
  border-radius: 20px;
  padding: 0.5rem 1rem;
}
/* .btn.btn-link:hover {
  background-color: var(--button-color);
  color: var(--background-mixed-color);
}*/
.btn:focus,
.btn:hover {
  background-color: var(--button-transparent-color);
}
#sidebar .form-search .form-input {
  max-width: unset;
  border-radius: 20px;
  height: 1.5rem;
}

.form-input.input-sm {
  border-radius: 20px;
  width: 90%;
  height: 1.5rem;
}

.form-input.input-sm {
  font-size: 0.7rem;
  height: 1.5rem;
  padding: 0.3rem 0.6rem;
  padding-right: 0.3rem;
}

#sidebar > * {
  padding-left: 0.3rem;
  padding-right: 0.3rem;
  padding-bottom: 0.3rem;
}

#sidebar .btn {
  width: 90%;
  align-self: center;
}

#sidebar .nav .btn-link {
  width: 100%;
  padding: 0.4rem 1rem;
  border: 2px solid var(--button-transparent-color);
}
